            super(object, "From Parent");
        }

        // @Transactional(readOnly = true)
        @Override
        public ConcreteSettings load() {
            return super.object;
        }
    }

    static abstract class AbstractDaoImpl implements Dao {

        protected T object;

        protected S otherObject;

        protected AbstractDaoImpl(T object, S otherObject) {
            this.object = object;
            this.otherObject = otherObject;
        }

        // @Transactional(readOnly = true)
        @Override
        public S loadFromParent() {
            return otherObject;
        }
    }

  public interface ConcreteSettingsDao extends SettingsDao {

        @Override
        String loadFromParent();
    }

    public interface SettingsDao extends Dao {

        @Override
        T load();
    }

    public interface Dao {

        T load();

        S loadFromParent();
    }

