//parentDefaults是父类的DocumentDefaultsDefinition对象
protected void populateDefaults(DocumentDefaultsDefinition defaults, DocumentDefaultsDefinition parentDefaults, Element root) {
     //查看xml文件中默认的default-lazy-init 值；(如果xml没有显示配置 则它的值为 default)懒加载的默认值
     String lazyInit = root.getAttribute(DEFAULT_LAZY_INIT_ATTRIBUTE);
     if (DEFAULT_VALUE.equals(lazyInit)) {
         //如果有父类,则以父类的为准，否则将返回false。
         lazyInit = (parentDefaults != null ? parentDefaults.getLazyInit() : FALSE_VALUE);
     }
     defaults.setLazyInit(lazyInit);
     //default-autowire-candidates
     String merge = root.getAttribute(DEFAULT_MERGE_ATTRIBUTE);
     if (DEFAULT_VALUE.equals(merge)) {
         // Potentially inherited from outer  sections, otherwise falling back to false.
         merge = (parentDefaults != null ? parentDefaults.getMerge() : FALSE_VALUE);
     }
     defaults.setMerge(merge);
     //default-autowire
     String autowire = root.getAttribute(DEFAULT_AUTOWIRE_ATTRIBUTE);
     if (DEFAULT_VALUE.equals(autowire)) {
         // Potentially inherited from outer  sections, otherwise falling back to 'no'.
         autowire = (parentDefaults != null ? parentDefaults.getAutowire() : AUTOWIRE_NO_VALUE);
     }
     defaults.setAutowire(autowire);

     // Don't fall back to parentDefaults for dependency-check as it's no longer supported in
     //  as of 3.0. Therefore, no nested  would ever need to fall back to it.
     defaults.setDependencyCheck(root.getAttribute(DEFAULT_DEPENDENCY_CHECK_ATTRIBUTE));

     if (root.hasAttribute(DEFAULT_AUTOWIRE_CANDIDATES_ATTRIBUTE)) {
         defaults.setAutowireCandidates(root.getAttribute(DEFAULT_AUTOWIRE_CANDIDATES_ATTRIBUTE));
     }
     else if (parentDefaults != null) {
         defaults.setAutowireCandidates(parentDefaults.getAutowireCandidates());
     }

     if (root.hasAttribute(DEFAULT_INIT_METHOD_ATTRIBUTE)) {
         defaults.setInitMethod(root.getAttribute(DEFAULT_INIT_METHOD_ATTRIBUTE));
     }
     else if (parentDefaults != null) {
         defaults.setInitMethod(parentDefaults.getInitMethod());
     }

     if (root.hasAttribute(DEFAULT_DESTROY_METHOD_ATTRIBUTE)) {
         defaults.setDestroyMethod(root.getAttribute(DEFAULT_DESTROY_METHOD_ATTRIBUTE));
     }
     else if (parentDefaults != null) {
         defaults.setDestroyMethod(parentDefaults.getDestroyMethod());
     }
     //这里是？？？
     defaults.setSource(this.readerContext.extractSource(root));
 }



